; OPTS -c configs/minimal.cfg
;
; MERA-400 floating point unit "TPZ" test.
;
; Based on the original test provided by the manufacturer.
; The only difference is that this version uses changed HLT codes.

	UJS init

; ------------------------------------------------------------------------
timerx:
ch1x:
ch3x:	LIP

; ------------------------------------------------------------------------
init:	MCL
	LW    r1, stack
	RW    r1, stackp
	IM    mask
	UJ    test_ad

; ------------------------------------------------------------------------
mask:	.word 0b1111111111000000
status:	.word 0

; ------------------------------------------------------------------------
fxp_ofx:LWT   r5, 0
	UJS   fpint
fp_ufx:	LW    r5, 0x20
	UJS   fpint
fp_ofx:	LW    r5, 0x40
	UJS   fpint
div0x:	LW    r5, 0x60

; ------------------------------------------------------------------------
fpint:	LW    r6, [stackp]
	AWT   r6, -4
	LW    r0, [r6+1]
	JXS   f1
	UJS   f2
f1:	LW    r7, 0x60
	BS    r0, r5
	UJS   f3
fret:	LW    r7, [r6]
	LW    r0, [r6+1]
	RW    r6, stackp
	IM    mask
	UJ    r7+1
f2:	LWS   r7, status
	CWT   r7, 0
	JES   fphlt1
	HLT   040
	UJS   fret
fphlt1:	HLT   040
	UJS   fret
f3:	LWS   r7, status
	CWT   r7, 0
	JES   fphlt2
	HLT   040
	UJS   fret
fphlt2:	HLT   040
	UJS   fret

; ------------------------------------------------------------------------
	.org 0x40
	.word powerx
	.word parx
	.word nomemx
	.word c2hix
	.word ifpwrx
	.word timerx
	.word opx
	.word fxp_ofx
	.word fp_ufx
	.word fp_ofx
	.word div0x
	.word extrax
	.word ch0x
