	LD    r3-4
	HLT   040
	UJ    init

; ------------------------------------------------------------------------
test_ad:
	RZ    status
	LW    r4, data_ad
loop1:	LW    r0, [r4]
	LD    r4+2
	AD    r4+4
	JXS   halt1
jmp1:	LJ    check_
	CW    r4, data_sd
	JES   test_sd
	UJS   loop1
halt1:	HLT   040
	UJS   jmp1

; ------------------------------------------------------------------------
