loop2:	LW    r0, [r4]
	LD    r4+2
	SD    r4+4
	JXS   halt2
jmp2:	LJ    check_
	CW    r4, data_mw
	JES   test_mw
	UJS   loop2
halt2:	HLT   040
	UJS   jmp2

; ------------------------------------------------------------------------
check_:	.res 1
